        let nombre = factura.cliente;
        acumulador[nombre] = (acumulador[nombre] || 0) + (Number(factura.total) || 0);
        return acumulador;
      }, {})
  )
  .map(([cliente, total]) => ({ 
    x: cliente.substring(0, 15) + (cliente.length > 15 ? '...' : ''),
    y: total 
  }))
  .sort((a, b) => b.y - a.y)
  .slice(0, 5)
}}

