{{ 
  (() => {
    // 1. Datos completos del SQL
    const datos = Get_Ventas_Total.data || [];

    // 2. Lo que seleccionaste en el filtro (Ahora son Nombres: ['Rappi', 'Visa'])
    const seleccion = MultiTreeSelect2.selectedOptionValues;

    // 3. Filtrado Inteligente
    // Si la lista de selección está vacía, sumamos TODO.
    // Si tiene algo, sumamos solo lo que coincida con el nombre.
    const datosFiltrados = (seleccion.length === 0) 
        ? datos 
        : datos.filter(item => seleccion.includes(item.Medio_Pago));

    // 4. Suma final
    return datosFiltrados.reduce((acc, item) => acc + item.Total_Venta_Real, 0)
      .toLocaleString('es-AR', { style: 'currency', currency: 'ARS', minimumFractionDigits: 0 });
  })()
}}
