{{ 
  (() => {
    const datos = Get_Ventas_Total.data || [];
    const seleccion = MultiTreeSelect2.selectedOptionValues;

    // Filtro inteligente: Si no seleccionaste nada, usa todo. Si seleccionaste, filtra.
    const datosFiltrados = (seleccion.length === 0) 
      ? datos 
      : datos.filter(item => seleccion.includes(item.Medio_Pago));

    // CORRECCIÃ“N: Ahora sumamos la columna 'Total_Facturado_Real'
    return datosFiltrados.reduce((acc, item) => acc + item.Total_Facturado_Real, 0)
      .toLocaleString('es-AR', { style: 'currency', currency: 'ARS', minimumFractionDigits: 2 });
  })()
}}
